import * as FIELDSNAMES from './components/CalculationSetup/calculationSetupConstants';

const validateSovosBasicText = (text) => {
  let textError = '';
  if (text) {
    if (text.length > 40) {
      textError = 'Maximun character limit is 40';
    } else {
      const patt = new RegExp(/^[a-zA-Z0-9\-\/]+$/);
      if (!patt.test(text)) {
        textError = 'Allowed character a-z A-Z 0-9 -/';
      }
    }
  }
  return textError;
}

const validateSovosAmountNumber = (amount) => {
  let amountError = '';
  if (isNaN(amount)) {
    amountError = 'This field must be a number';
  } else {
    const patt = new RegExp(/^\d{1,12}(\.\d{1,8})?$/);
    if (!patt.test(amount)) {
      amountError = 'Character Limit: 12 characters before the decimal point and 8 characters after';
    }
  }
  return amountError
}


const taxCalculatorValidate = (values) => {
  const errors = {};

  /* Validate document number field name */
  errors[FIELDSNAMES.DOCUMENT_NUMBER_FIELD_NAME] =
    validateSovosBasicText(values[FIELDSNAMES.DOCUMENT_NUMBER_FIELD_NAME]);


  /* Validate Additional fields */
  errors[FIELDSNAMES.ADDITIONAL_FIELDS_NAME] = [];
  const additionalFields = values[FIELDSNAMES.ADDITIONAL_FIELDS_NAME];
  errors[FIELDSNAMES.ADDITIONAL_FIELDS_NAME] = additionalFields.map(field => {
      return validateAdditionalField(field)
    }
  );

  return errors;
};

const validateAdditionalField = (field) => {
  switch (field.type) {
    case FIELDSNAMES.CUSTOMS_DUTIES_FIELD_NAME: {
      return { amount: validateSovosAmountNumber(field.amount) }
    }

    case FIELDSNAMES.DELYVERY_AMOUNT_FIELD_NAME: {
      return { amount: validateSovosAmountNumber(field.amount) }
    }
    case FIELDSNAMES.DELYVERY_INSURANCE_FIELD_NAME: {
      return { amount: validateSovosAmountNumber(field.amount) };
    }
    case FIELDSNAMES.TRANSACTION_SOURCE_FIELD_NAME: {
      return { transactionSource: validateSovosBasicText(field.transactionSource) };
    }

    default: {
      return {}
    }

  }
}

export default taxCalculatorValidate;
