import ActionDelete from '@material-ui/icons/Clear';

import Divider from '@material-ui/core/Divider';
import MenuItem from '@material-ui/core/MenuItem';
import { withStyles } from '@material-ui/core/styles';
import classNames from 'classnames';
import Tooltip from '@material-ui/core/Tooltip';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import { DatePicker } from 'material-ui-pickers';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Menu from '@material-ui/core/Menu';
import { Button } from '@material-ui/core';

const styles = (theme) => {
  console.log('theme= ', theme);
  return ({
    mt3: {
      marginTop: 3 * theme.spacing.unit
    },
    mt2: {
      marginTop: 2 * theme.spacing.unit
    },
    mt1: {
      marginTop: theme.spacing.unit
    },
    w75: {
      width: '75%'
    },
    mlN1: {
      marginLeft: '-1px'
    },
    card: { marginBottom: '20px' },
    cardContent: { padding: '20px', borderTop: '1px solid #e6e6e6' },
    cardContent2: { padding: '0px !important' },
    cardHeader: { padding: '20px' }
  })
}

const renderSovosTextField = ({ tooltip, label, input, meta: { touched, invalid, error }, ...custom }) => {
  const textField = (
    <SovosTextField
      label={label}
      error={touched && invalid}
      helperText={touched && error}
      {...input}
      {...custom}
    />
  );
  return tooltip ? (
    <Tooltip title={tooltip}>
      {textField}
    </Tooltip>
  ) : (
      <>
        {textField}
      </>
    )
}

const renderSovosDatePicker = ({ tooltip, label, input, meta: { touched, invalid, error }, ...custom }) => {
  const datePicker = (<DatePicker
    label={label}
    placeholder={label}
    error={touched && invalid}
    helperText={touched && error}
    {...input}
    {...custom}
  />)

  return datePicker
}

const renderFromHelper = ({ touched, error }) => {
  if (!(touched && error)) {
    return
  } else {
    return <FormHelperText>{touched && error}</FormHelperText>
  }
}

const renderSovosSelectField = ({ input, name, topLabel, label, meta: { touched, error }, children, ...custom }) => (
  <FormControl error={touched && error} {...custom}>
    <InputLabel>{label}</InputLabel>
    <SovosSelect {...input}
      {...custom}

    >
      {children}
    </SovosSelect>
    {renderFromHelper({ touched, error })}
  </FormControl>
)

const renderSovosCheckboxField = ({ input, label, className }) => (
  <FormControlLabel
    className={className}
    control={
      <UnstyledSovosCheckbox
        checked={input.value ? true : false}
        onChange={input.onChange}
      />
    }
    label={label}
  />
)

