const renderSetupCardAdditionalFields = ({ fields, meta: { error, submitFailed } }) => {


  const items = [
    {
      type: 'custmsDutTrfAmt',
      display: 'Customs/Duties/Tariffs Amount'
    },
    {
      type: 'dlvrAmt',
      display: 'Delivery Amount'
    },
    {
      type: 'dlvrInsAmt',
      display: 'Delivery Insurance Amount'
    },
    {
      type: 'trnSrc',
      display: 'Transaction Source'
    }
  ];
  return (
    <div>
      <div>
        {submitFailed && error && <span>{error}</span>}
      </div>
      <div style={{ padding: '20px', borderBottom: '1px solid #e6e6e6' }}>
        {fields.map((fieldPath, index) => {
          switch (fields.get(index).type) {
            case 'custmsDutTrfAmt': {
              return <CustmsDutTrfAmt
                key={index}
                fieldPath={fieldPath}
                onRemoveField={() => fields.remove(index)}
              />
            }
            case 'dlvrAmt': {
              return <DlvrAmt
                key={index}
                fieldPath={fieldPath}
                onRemoveField={() => fields.remove(index)}
              />
            }
            case 'dlvrInsAmt': {
              return <DlvrInsAmt
                key={index}
                fieldPath={fieldPath}
                onRemoveField={() => fields.remove(index)}
              />
            }
            case 'trnSrc': {
              return <TrnSrc
                key={index}
                fieldPath={fieldPath}
                onRemoveField={() => fields.remove(index)}
              />
            }
            default: {
              return <h1>Pow</h1>
            }
          }

        })
        }
      </div>
      <div style={{padding: '20px'}}>
        <SovosButtonWithMenus
          label="Add Calculation Set-up Items"
          items={items}
          onItemSelected={(item) => fields.push({ type: item.type })}
          itemsSelected={fields.getAll()}
        />
      </div>
    </div>
  )
}

function SovosButtonWithMenus({ label, items, itemsSelected, onItemSelected, className, ...custom }) {
  if (items.length === itemsSelected.length) {
    return (<></>);
  }
  const [anchorEl, setAnchorEl] = React.useState(null);

  function handleClick(event) {
    setAnchorEl(event.currentTarget);
  }

  function handleClose() {
    setAnchorEl(null);
  }

  function handleItemSelected(item) {
    onItemSelected(item)
    handleClose()
  }

  return (
    <div className={className} style={{ display: 'flex', justifyContent: 'flex-end', marginTop: '18px' }}>
      <Button
        variant="outlined"
        aria-owns={anchorEl ? 'simple-menu' : undefined}
        aria-haspopup="true"
        onClick={handleClick}
      >
        {label}
      </Button>
      <Menu id="simple-menu" anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={handleClose}>
        {items.filter(item => !itemsSelected.find(itemSelected => itemSelected.type === item.type))
          .map((item, key) => (
            <MenuItem key={key} onClick={() => handleItemSelected(item)}>{item.display}</MenuItem>
          ))}
      </Menu>
    </div>
  )
}

