import React from 'react';
import PropTypes from 'prop-types';
import Card from '@material-ui/core/Card';
import { withStyles } from '@material-ui/core/styles';
import CardHeader from '@material-ui/core/CardHeader';
import CardContent from '@material-ui/core/CardContent';
import RadioDebitCredit from './RadioDebitCredit';
import MenuItem from '@material-ui/core/MenuItem';
import AddIcon from '@material-ui/icons/Add';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import SovosSelect from 's1-ui/sovos-select';
import green from '@material-ui/core/colors/green';
import { Field, reduxForm } from 'redux-form';
import Radio from '@material-ui/core/Radio'
import SelectTransactionType from './SelectTransactionType';
import { required } from './validation';

const styles = {
    cardContent: { padding: '20px', borderTop: '1px solid #e6e6e6', borderBottom: '1px solid #e6e6e6' },
    cardHeader: { padding: '20px' },
    dropdownAddForm: { width: '40%' },
    footer: { padding: '20px', display: 'flex', padding: '16px', alignItems: 'center', justifyContent: 'flex-end' },
    iconAdd: { color: green[500], marginTop: '0.8em' }
};

function LineItemDefault(props) {
    const { classes } = props;
    return (
        <Card>
            <CardHeader
                className={classes.cardHeader}
                title="Line Items Defaults"
                subheader="Settings in this section apply to all added Data Point Line items; and can be edited per line" />
            <CardContent
                className={classes.cardContent}
            >
                <Field name="cardType" component={RadioDebitCredit}>
                    <Radio value="debit" label="Debit" />
                    <Radio value="credit" label="Credit" />
                </Field>
                <Field
                    validate={[required]}
                    classes={classes}
                    name="transactionType"
                    component={SelectTransactionType}
                />
            </CardContent>
            <div className={classes.footer}>
                <AddIcon className={classes.iconAdd} />
                <FormControl className={classes.dropdownAddForm}>
                    <InputLabel htmlFor="dropdown-add-form">Add Line Item Defaults</InputLabel>
                    <SovosSelect
                        // onChange={e => setState({ value: e.target.value })}
                        displayEmpty
                        value={''}
                        inputProps={{
                            name: 'dropdownAddForm',
                            id: 'dropdown-add-form'
                        }}
                    >
                    </SovosSelect>
                </FormControl>
            </div>
        </Card>
    );
}

LineItemDefault.propTypes = {};
LineItemDefault.defaultProps = {};

export default withStyles(styles)(LineItemDefault);
