import React from 'react';
import PropTypes from "prop-types";
import FormControl from '@material-ui/core/FormControl';
import MenuItem from '@material-ui/core/MenuItem';
import InputLabel from '@material-ui/core/InputLabel';
import SovosSelect from 's1-ui/sovos-select';
import { withStyles } from '@material-ui/core/styles';
import renderFromHelper from './ErrorText';

const styles = {
    transactionType: { width: '40%' }
};

const transactionType = [
    {
        id: 1, name: 'Sale'
    },
    {
        id: 2, name: 'Purchase'
    },
    {
        id: 3, name: 'Rental, Option to Own (A/R)'
    },
    {
        id: 4, name: 'Rental, Option to Own (A/P)'
    },
    {
        id: 5, name: 'Rental, No Option to Own (A/R)'
    },
    {
        id: 6, name: 'Rental, No Option to Own (A/P)'
    },
    {
        id: 7, name: 'Consignment Sale'
    },
    {
        id: 8, name: 'Consignment Purchase'
    },
    {
        id: 9, name: 'Installation Sale'
    },
    {
        id: 14, name: 'Return (A/R)'
    },
    {
        id: 15, name: 'Return (A/P)'
    },
    {
        id: 18, name: 'Evaluated Receipts Settlements'
    },
    {
        id: 19, name: 'Lease oper first payment'
    },
    {
        id: 20, name: 'Lease oper stream payment'
    },
    {
        id: 21, name: 'Lease oper quote on contract'
    },
    {
        id: 22, name: 'Lease oper early termination'
    },
    {
        id: 23, name: 'Lease nom first payment'
    },
    {
        id: 24, name: 'Lease nom stream payment'
    },
    {
        id: 25, name: 'Lease nom quote on contract'
    },
    {
        id: 26, name: 'Lease nom buyout payment'
    },
    {
        id: 27, name: 'Lease nom early termination with buyout'
    },
    {
        id: 28, name: 'Lease nom early termination w/o buyout'
    },
    {
        id: 29, name: 'Lease fmv first payment'
    },
    {
        id: 30, name: 'Lease fmv stream payment'
    },
    {
        id: 31, name: 'Lease fmv quote on contract'
    },
    {
        id: 32, name: 'Lease fmv buyout payment'
    },
    {
        id: 33, name: 'Lease fmv early termination with buyout'
    },
    {
        id: 34, name: 'Lease fmv early termination w/o buyout'
    },
    {
        id: 35, name: 'Conditional sale quote on contract'
    },
    {
        id: 36, name: 'Conditional sale first payment'
    },
    {
        id: 37, name: 'Conditional sale stream payment'
    },
    {
        id: 38, name: 'Conditional sale early termination'
    },
    {
        id: 39, name: 'Stock Movement Dispatch'
    },
    {
        id: 40, name: 'Stock Movement Arrival'
    },
    {
        id: 41, name: 'Customs Warehouse Dispatch'
    },
    {
        id: 42, name: 'Customs Warehouse Arrival'
    },
    {
        id: 43, name: 'Intra Company Sale'
    },
    {
        id: 44, name: 'Intra Company Purchase'
    },
    {
        id: 45, name: 'Intra VAT Group Sale'
    },
    {
        id: 46, name: 'Intra VAT Group Purchase'
    },
    {
        id: 47, name: 'Intra Customs Warehouse Transaction(A/R)'
    },
    {
        id: 48, name: 'Intra Customs Warehouse Transaction(A/P)'
    },
    {
        id: 49, name: 'Commissionaire Sale'
    },
    {
        id: 50, name: 'Call Off Stock Sale'
    },
    {
        id: 51, name: 'Call Off Stock Purchase'
    },
    {
        id: 52, name: 'Nominal Lease Quote On K-TTaI'
    },
    {
        id: 53, name: 'Nominal Lease 1st Payment-TTaI'
    },
    {
        id: 54, name: 'Nominal Lease Stream Payment-TTaI'
    }
]

const SelectTransactionType = ({
    input,
    label,
    classes,
    meta: { touched, error },
    // children,
    ...custom
}) => (
        <FormControl error={!!(touched && error)} className={classes.transactionType}>
            <InputLabel htmlFor="transaction-type">transaction type</InputLabel>
            <SovosSelect
                // onChange={e => setState({ value: e.target.value })}
                {...input}
                {...custom}
                displayEmpty
                inputProps={{
                    name: 'transactionType',
                    id: 'transaction-type'
                }}
            >
                {transactionType.map(v => <MenuItem key={v.id} value={v.id}>{v.name}</MenuItem>)}
            </SovosSelect>
            {renderFromHelper({ touched, error })}
        </FormControl>
    )

SelectTransactionType.propTypes = {};
SelectTransactionType.defaultProps = {};

export default withStyles(styles)(SelectTransactionType);
