import React, { Component } from 'react';
import { Field, FieldArray } from 'redux-form';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardHeader from '@material-ui/core/CardHeader';
import SovosIconButton from 's1-ui/sovos-icon-button';

import ActionDelete from '@material-ui/icons/Clear';
import MenuItem from '@material-ui/core/MenuItem';
import { withStyles } from '@material-ui/core/styles';
import classNames from 'classnames';
import renderSovosTextField from '../../../util/renderSovosTextField';
import renderSovosDatePicker from '../../../util/renderSovosDatePicker';
import renderSovosSelectField from '../../../util/renderSovosSelectField';
import renderSovosCheckboxField from '../../../util/renderSovosCheckboxField';
import SovosSimpleDynamicTextField from '../../../util/SovosSimpleDynamicTextField';
import * as FIELDSNAMES from './calculationSetupConstants';
import SovosButtonShowMenus from '../../../util/SovosButtonShowMenus';

const styles = (theme) => {
  return ({
    mt3: {
      marginTop: 3 * theme.spacing.unit
    },
    mt2: {
      marginTop: 2 * theme.spacing.unit
    },
    mt5: {
      marginTop: theme.spacing.unit * 5
    },
    w75: {
      width: '75%'
    },
    mlN1: {
      marginLeft: '-1px'
    },
    w250: {
      width: '250px'
    },
    w480: {
      width: '480px'
    },
    mgTop40: { marginTop: '40px !important' },
    mgBottom20: { marginBottom: '20px !important' },
    borderCustom: { borderBottom: '0.7px solid #e6e6e6' },
    card: { marginBottom: '20px' },
    cardContent: { padding: '20px', borderTop: '1px solid #e6e6e6' },
    cardContentAdditionalField: { padding: '0px !important' },
    cardHeader: { padding: '20px' },
    footer: { padding: '20px', display: 'flex', padding: '16px', alignItems: 'center', justifyContent: 'flex-end' }
  })
}

const Discounts = ({ fieldPath, onRemoveField }) => {
  return (
    <div>
      <div style={ { display: 'flex', justifyContent: 'space-between' } }>
        <h5>Discounts</h5>
        <SovosIconButton
          size="medium"
          onClick={ onRemoveField }
        >
          <ActionDelete />
        </SovosIconButton>
      </div>
      <Field
        name={ `${ fieldPath }.amount` }
        component={ renderSovosTextField }
        label="Discount"
      />
    </div>
  )

}

const renderSetupCardAdditionalFields = ({ fields, meta: { error, submitFailed } }) => {
  const items = [
    {
      type: FIELDSNAMES.CUSTOMS_DUTIES_FIELD_NAME,
      display: 'Customs/Duties/Tariffs Amount'
    },
    {
      type: FIELDSNAMES.DELYVERY_AMOUNT_FIELD_NAME,
      display: 'Delivery Amount'
    },
    {
      type: FIELDSNAMES.DELYVERY_INSURANCE_FIELD_NAME,
      display: 'Delivery Insurance Amount'
    },
    {
      type: FIELDSNAMES.TRANSACTION_SOURCE_FIELD_NAME,
      display: 'Transaction Source'
    },
    {
      type: FIELDSNAMES.DISCOUNTS_FIELDS_NAME,
      display: 'Discounts'
    }
  ];
  return (
    <div>
      <div>
        { submitFailed && error && <span>{ error }</span> }
      </div>
      <div style={ {
        padding: '20px',
        borderBottom: '1px solid #e6e6e6',
        borderTop: fields.length && '1px solid #e6e6e6'
      } }>
        {
          fields.map((fieldPath, index) => {
            switch (fields.get(index).type) {
              case FIELDSNAMES.CUSTOMS_DUTIES_FIELD_NAME: {
                return (<SovosSimpleDynamicTextField
                  key={ index }
                  labelInput="Amount"
                  title="Customs/Duties Amount"
                  fieldName={ `${ fieldPath }.amount` }
                  onRemoveField={ () => fields.remove(index) }
                  type="number"
                />)
              }
              case FIELDSNAMES.DELYVERY_AMOUNT_FIELD_NAME: {
                return <SovosSimpleDynamicTextField
                  key={ index }
                  labelInput="Amount"
                  title="Delvery Amount"
                  fieldName={ `${ fieldPath }.amount` }
                  onRemoveField={ () => fields.remove(index) }
                  type="number"
                />
              }
              case FIELDSNAMES.DELYVERY_INSURANCE_FIELD_NAME: {
                return <SovosSimpleDynamicTextField
                  key={ index }
                  labelInput="Amount"
                  title="Delivery Insurance Amount"
                  fieldName={ `${ fieldPath }.amount` }
                  onRemoveField={ () => fields.remove(index) }
                />
              }
              case FIELDSNAMES.TRANSACTION_SOURCE_FIELD_NAME: {
                return <SovosSimpleDynamicTextField
                  key={ index }
                  labelInput="Transaction Source"
                  title="Transaction Source"
                  fieldName={ `${ fieldPath }.transactionSource` }
                  onRemoveField={ () => fields.remove(index) }
                />;
              }
              case 'discounts': {
                return <SovosSimpleDynamicTextField
                  key={ index }
                  fieldPath={ fieldPath }
                  onRemoveField={ () => fields.remove(index) }
                />;

              }
              default: {
                return <h1>Pow</h1>
              }
            }

          })
        }
      </div>
      <div style={ { padding: '20px' } }>
        <SovosButtonShowMenus
          label="Add Calculation Set-up Items"
          items={ items }
          onItemSelected={ (item) => {
            fields.push({ type: item.type })
          } }
          itemsSelected={[]}
        />

      </div>

    </div>
  )
}

class TaxCaculatorForm extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    let { classes } = this.props;
    return (
      <Card className={ classes.card }>
        <CardHeader
          className={ classes.cardHeader }
          title="Calculation Set-up"
          subheader="Settings in this section apply to the entire caculation" />
        <CardContent className={ classes.cardContent }>
          {/* <div className="card-header">
            <Typography variant="h6" color="textPrimary">
              Calculation Set-up
            </Typography>
            <Typography color="textSecondary" gutterBottom>
              Settings in this section apply to the entire caculation
            </Typography>
          </div> */ }
          {/* <Divider /> */ }
          {/* <div style={{ display: 'flex', flexDirection: 'column' }}> */ }
          <div className={ classes.mgTop40 }>
            <Field
              tooltip="Leave blank to apply the system date"
              className={ classNames(classes.w250) }
              name='docDt'
              label="Document Date"
              component={ renderSovosDatePicker }
            /> {/*Duccument Date*/ }
          </div>


          <div className={ classes.mgTop40 }>
            <Field
              className={ classNames(classes.w250) }
              name='trnDocNum'
              label="Document Number"
              component={ renderSovosTextField }
            /> {/*Document number*/ }
          </div>


          <div className={ classes.mgTop40 }>
            <Field
              className={ classNames(classes.borderCustom, classes.w480) }
              name='txCalcTp'
              label="Tax Calculation Type"
              component={ renderSovosSelectField }
            >
              <MenuItem value={ 1 }>Forward calculation</MenuItem>
              <MenuItem value={ 2 }>Tax inclusive gross amount back calculation</MenuItem>
              <MenuItem value={ 5 }>Tax to gross calculation with force distribution</MenuItem>
              <MenuItem value={ 6 }>Tax to gross back calculation</MenuItem>
            </Field> {/*Tax cal type*/ }
          </div>
          <div className={ classes.mgTop40 }>
            <Field
              className={ classNames(classes.borderCustom, classes.w250) }
              name='currn'
              label="Currency Type"
              component={ renderSovosSelectField }
            >
              <MenuItem value={ "USD" }>USD</MenuItem>
              <MenuItem value={ "CAD" }>CAD</MenuItem>
              <MenuItem value={ "AUD" }>AUD</MenuItem>
              <MenuItem value={ "EUR" }>EUR</MenuItem>
              <MenuItem value={ "GBP" }>GBP</MenuItem>
              <MenuItem value={ "NZD" }>NZD</MenuItem>
              <MenuItem value={ "MXN" }>MXN</MenuItem>
              <MenuItem value={ "SEK" }>SEK</MenuItem>
              <MenuItem value={ "JPY" }>JPY</MenuItem>
              <MenuItem value={ "NOK" }>NOK</MenuItem>
              <br />
              <MenuItem value={ "VND" }>VND</MenuItem>
            </Field> {/*curr type*/ }

          </div>


          <div className={ classNames(classes.mgBottom20, classes.mgTop40) }>
            <Field
              className={ classNames(classes.mlN1, classes.w250) }
              name="isAudit"
              component={ renderSovosCheckboxField }
              label="Write to Audit"
            /> {/*write to audits*/ }
