import React from 'react';
import { Button } from '@material-ui/core';
import AddIcon from '@material-ui/icons/Add';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';


export default function SovosButtonShowMenus({ label, items, itemsSelected, onItemSelected, className, ...custom }) {
  if (items.length === itemsSelected.length) {
    return (<></>);
  }
  const [anchorEl, setAnchorEl] = React.useState(null);

  function handleClick(event) {
    setAnchorEl(event.currentTarget);
  }

  function handleClose() {
    setAnchorEl(null);
  }

  function handleItemSelected(item) {
    onItemSelected(item)
    handleClose();
  }

  return (
    <div className={ className } style={ { display: 'flex', justifyContent: 'flex-end', marginTop: '18px' } }>
      <Button
        variant="outlined"
        aria-owns={ anchorEl ? 'simple-menu' : undefined }
        aria-haspopup="true"
        onClick={ handleClick }
      >
        <AddIcon />
        { label }
      </Button>
      <Menu id="simple-menu" anchorEl={ anchorEl } open={ Boolean(anchorEl) } onClose={ handleClose }>
        { items.filter(item => !itemsSelected.find(itemSelected => itemSelected.type === item.type))
          .map((item, key) => (
            <MenuItem key={ key } onClick={ () => handleItemSelected(item) }>{ item.display }</MenuItem>
          )) }
      </Menu>
    </div>
  )
}
