import React from 'react';
import PropTypes from "prop-types";
import FormControl from '@material-ui/core/FormControl';
import MenuItem from '@material-ui/core/MenuItem';
import InputLabel from '@material-ui/core/InputLabel';
import SovosSelect from 's1-ui/sovos-select';
import { withStyles } from '@material-ui/core/styles';
import renderFromHelper from './ErrorText';
import classNames from 'classnames';

import DataTransaction from '../../constants/LineItemDefaultTransactionType';

const styles = {
    transactionType: { width: '40%', borderBottom: '0.7px solid #e6e6e6' }
};

const transactionType = DataTransaction;

const SelectTransactionType = ({
    input,
    label,
    classes,
    meta: { touched, error },
    // children,
    ...custom
}) => (
        <FormControl error={!!(touched && error)} className={classes.transactionType}>
            <InputLabel htmlFor="transaction-type">transaction type</InputLabel>
            <SovosSelect
                // onChange={e => setState({ value: e.target.value })}
                {...input}
                {...custom}
                displayEmpty
                inputProps={{
                    name: 'transactionType',
                    id: 'transaction-type'
                }}
            >
                {transactionType.map(v => <MenuItem key={v.id} value={v.id}>{v.name}</MenuItem>)}
            </SovosSelect>
            {renderFromHelper({ touched, error })}
        </FormControl>
    )

SelectTransactionType.propTypes = {};
SelectTransactionType.defaultProps = {};

export default withStyles(styles)(SelectTransactionType);
