import React from 'react';
import SovosIconButton from 's1-ui/sovos-icon-button';
import ActionDelete from '@material-ui/icons/Clear';
import { FieldArray } from 'redux-form';
import SovosRaiseButton from 's1-ui/sovos-raised-button';

import Discount from './Discount';

const renderDiscounts = ({ fields, meta: { error, submitFailed } }) => {
  return (
    fields.map((field, index) => {

      return (
        <div style={ { display: 'flex', alignItems: 'flex-end' } } key={ index }>
          <Discount
            typeInputName={ `${ field }.type` }
            amountInputName={ `${ field }.amount` }
          />

          { fields.length > 1 || index > 0 ? (
            <SovosRaiseButton
              style={ { marginLeft: 25 } }
              color="secondary"
              onClick={ () => fields.remove(index) }
            >
              Remove
            </SovosRaiseButton>) : <></> }

          <span style={ { flexGrow: 1, flexShrink: 1, flexBasis: 'auto' } }></span>
          { index + 1 === fields.length && fields.length < 5 ?
            (<SovosRaiseButton
              style={ { alignSeft: 'flex-end' } }
              color="secondary"
              onClick={ () => fields.push({ type: 1, amount: '' }) }
            >
              Add another type
            </SovosRaiseButton>) : (<></>)
          }

        </div>
      )


    })
  )
}

function Discounts({ fieldName, discounts, onRemoveDiscounts }) {
  // const { discounts, onRemoveDiscounts } = props;
  return (
    <div>
      <div style={ { display: 'flex', justifyContent: 'space-between', marginTop: 20 } }>
        <h5>Discounts</h5>
        <SovosIconButton
          size="medium"
          onClick={ onRemoveDiscounts }
        >
          <ActionDelete />
        </SovosIconButton>
      </div>
      <FieldArray name={ fieldName } component={ renderDiscounts } />
    </div>
  );
}

Discounts.propTypes = {};
Discounts.defaultProps = {};

export default Discounts;
